/*Criar um trigger que, quando for inserida uma nova entrada de produtos
na tbEntradaProduto, a quantidade desse produto seja atualizada e
aumentada na tabela tbProduto;*/CREATE TRIGGER tgSaidaProduto ON tbItemVendaAFTER INSERTASBEGIN	DECLARE @codProdutoVendido INT, @qtdProdutoVendido INT	SET @codProdutoVendido = (SELECT codProduto FROM inserted)	SET @qtdProdutoVendido = (SELECT qtdItemVenda FROM inserted)	INSERT INTO tbSaidaProduto(codProduto, quantidadeSaidaProduto) VALUES 	(@codProdutoVendido, @qtdProdutoVendido)END-- EXECUTE OS BLOCOS ABAIXO JUNTOS PARA VISUALIZAÇÃOSELECT * FROM tbItemVenda WHERE codProduto = 1INSERT INTO tbItemVenda (codVenda, codProduto, qtdItemVenda, subTotalItemVenda) VALUES(1, 1, 120, 1200)SELECT * FROM tbSaidaProduto WHERE codProduto = 1